	handleValidation(){
		console.log('vietnam');
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Name
        if(!fields["first_name"]){
        	console.log('vietnamNoName');
           formIsValid = false;
           errors["first_name"] = "Cannot be empty";
        }

        if(typeof fields["first_name"] !== "undefined"){
        	console.log('vietnamName');
           if(!fields["first_name"].match(/^[a-zA-Z]+$/)){
              formIsValid = false;
              errors["first_name"] = "Only letters";
           }        
        }

         //Name
        if(!fields["last_name"]){
        	console.log('vietnamNoLastName');
           formIsValid = false;
           errors["last_name"] = "Cannot be empty";
        }

        if(typeof fields["last_name"] !== "undefined"){
        	console.log('vietnamLastName');
           if(!fields["last_name"].match(/^[a-zA-Z]+$/)){
              formIsValid = false;
              errors["last_name"] = "Only letters";
           }        
        }

        //Email
        if(!fields["email"]){
        	console.log('vietnamNoEmail');
           formIsValid = false;
           errors["email"] = "Cannot be empty";
        }

        if(typeof fields["email"] !== "undefined"){
        	console.log('vietnamEmail');
           let lastAtPos = fields["email"].lastIndexOf('@');
           let lastDotPos = fields["email"].lastIndexOf('.');

           if (!(lastAtPos < lastDotPos && lastAtPos > 0 && fields["email"].indexOf('@@') == -1 && lastDotPos > 2 && (fields["email"].length - lastDotPos) > 2)) {
              formIsValid = false;
              errors["email"] = "Email is not valid";
            }

        if(!fields["phone"]){
        	console.log('vietnamNoPhone');
           formIsValid = false;
           errors["email"] = "Cannot be empty";
        }
        console.log(this.state);
       } 

       this.setState({errors: errors});
       return formIsValid;
   	}

   	contactSubmit(e){
        e.preventDefault();
        if(this.handleValidation()){
        	console.log('7');
           alert("Form submitted");
        }else{
        	console.log('7');
           alert("Form has errors.")
        }

    }

    handleChange(field, e){  

	        let fields = this.state.fields;
	        fields[field] = e.target.value;        
	        this.setState({fields});

    }


