				tax:'',
				totalAmount:''
			}		
		}
		else{
			console.log('control');
			this.state = {
				data:'',
				total:'',
				shipping:9.99,
				tax:'',
				totalAmount:''
			}
		}
	}

	state={

	}

	render(){
		// console.log(this.state);
		if(this.state.data === ''){
			return(
				<div className='shoppingCartArea'>
